import React, { useEffect, useState } from "react";

interface Deduction {
  category_name: string;
  avg_deduction_percentage: number;
  title: string;
  description: string;
  total_amount: number;
  potential_deduction?: number;
  transaction_count?: number;
}

const DeductionsWidget = () => {
  const [deductions, setDeductions] = useState<Record<string, Deduction[]>>({});
  const [activeTab, setActiveTab] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>("");
  const [isOpen, setIsOpen] = useState<boolean>(false);

  useEffect(() => {
    fetchDeductions();
  }, []);

  const fetchDeductions = async () => {
    try {
      setLoading(true);
      const response = await fetch(
        `https://dev-categorization.musetax.com/v2/api/potential-deductions/68624589068ff5fea74cf7be`
      );
      const data = await response.json();

      if (data.deductions) {
        const grouped = groupByCategory(data.deductions);
        setDeductions(grouped);
        setActiveTab(Object.keys(grouped)[0] || "");
      } else {
        throw new Error("No deductions data received");
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to fetch deductions");
    } finally {
      setLoading(false);
    }
  };

  const groupByCategory = (data: Deduction[]): Record<string, Deduction[]> => {
    return data.reduce((acc, deduction) => {
      const category = deduction.category_name || "Other";
      if (!acc[category]) {
        acc[category] = [];
      }
      acc[category].push(deduction);
      return acc;
    }, {} as Record<string, Deduction[]>);
  };

  const calculateTotals = (deductions: Deduction[]) => {
    const totalAmount = deductions.reduce((sum, d) => sum + (d.total_amount || 0), 0);
    const totalDeduction:any = deductions.reduce((sum:any, d:any) => sum+(d.potential_deduction || (d.total_amount * (d.avg_deduction_percentage / 100)), 0));

    return {
      totalAmount: totalAmount.toFixed(2),
      totalDeduction: totalDeduction.toFixed(2),
      savingsRange: calculateSavingsRange(totalDeduction)
    };
  };

  const calculateSavingsRange = (totalDeduction: number) => {
    const min = totalDeduction * 0.2;
    const max = totalDeduction * 0.3;
    return `$${min.toFixed(0)} â€“ $${max.toFixed(0)}`;
  };

  const toggleWidget = () => setIsOpen(!isOpen);

  if (error) {
    return (
      <div className="fixed bottom-4 right-4 z-50">
        <button
          onClick={toggleWidget}
          className="w-14 h-14 bg-blue-600 rounded-full flex items-center justify-center text-white text-2xl shadow-lg hover:bg-blue-700 transition"
        >
          ðŸ’¬
        </button>
        {isOpen && (
          <div className="fixed bottom-24 right-4 w-96 max-h-[70vh] bg-white rounded-xl shadow-xl border border-gray-200 overflow-hidden z-40">
            <div className="p-4 text-red-500">
              Error: {error}
            </div>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="fixed bottom-4 right-4 z-50">
      <button
        onClick={toggleWidget}
        className="w-14 h-14 bg-blue-600 rounded-full flex items-center justify-center text-white text-2xl shadow-lg hover:bg-blue-700 transition"
        title="Potential Deductions"
      >
        ðŸ’¬
      </button>

      {isOpen && (
        <div className="fixed bottom-24 right-4 w-96 max-h-[70vh] bg-white rounded-xl shadow-xl border border-gray-200 overflow-hidden z-40">
          {loading ? (
            <div className="p-6 flex justify-center items-center h-64">
              <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
          ) : (
            <>
              {/* Header */}
              <div className="bg-blue-50 px-6 py-4 flex justify-between items-center border-b border-blue-200">
                <h2 className="text-xl font-medium text-blue-900">
                  Potential Deductions Analysis
                </h2>
                <span className="text-sm text-gray-600 flex items-center gap-1">
                  <span className="w-2 h-2 rounded-full bg-blue-500"></span>
                  Analysis complete
                </span>
              </div>

              {/* Content */}
              <div className="p-6">
                {/* Tabs - Dynamic based on categories */}
                <div className="flex gap-2 mb-6 overflow-x-auto pb-2">
                  {Object.keys(deductions).map((category) => (
                    <button
                      key={category}
                      onClick={() => setActiveTab(category)}
                      className={`px-4 py-2 text-sm font-medium rounded-lg border border-blue-500 whitespace-nowrap ${
                        activeTab === category
                          ? "bg-blue-500 text-white"
                          : "bg-white text-blue-500"
                      }`}
                    >
                      {category}
                    </button>
                  ))}
                </div>

                {/* Deduction Items */}
                <div className="space-y-4 mb-6 max-h-[40vh] overflow-y-auto">
                  {deductions[activeTab]?.map((deduction, index) => (
                    <div
                      key={index}
                      className="border border-blue-100 rounded-xl p-4 hover:bg-gray-50 transition"
                    >
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="text-lg font-medium text-blue-900">
                            {deduction.title}
                          </h3>
                          <p className="text-sm text-gray-600">
                            {deduction.description}
                          </p>
                          <div className="mt-2 text-xs text-blue-400 flex items-center gap-1">
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              width="16"
                              height="16"
                              viewBox="0 0 20 20"
                              fill="none"
                            >
                              <path
                                d="M11.6667 1.66663V4.99996C11.6667 5.44199 11.8423 5.86591 12.1549 6.17847C12.4674 6.49103 12.8913 6.66663 13.3334 6.66663H16.6667M12.5 1.66663H5.00004C4.55801 1.66663 4.13409 1.84222 3.82153 2.15478C3.50897 2.46734 3.33337 2.89127 3.33337 3.33329V16.6666C3.33337 17.1087 3.50897 17.5326 3.82153 17.8451C4.13409 18.1577 4.55801 18.3333 5.00004 18.3333H15C15.4421 18.3333 15.866 18.1577 16.1786 17.8451C16.4911 17.5326 16.6667 17.1087 16.6667 16.6666V5.83329L12.5 1.66663Z"
                                stroke="#1E40AF"
                                strokeWidth="1.4"
                                strokeLinecap="round"
                                strokeLinejoin="round"
                              />
                            </svg>
                            <span>
                              {deduction.transaction_count 
                                ? `Based on ${deduction.transaction_count} transactions`
                                : `Multiple transactions`}
                            </span>
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="text-blue-900 text-lg font-medium mb-1">
                            ${deduction.total_amount?.toFixed(2)}
                          </div>
                          <div className="text-sm text-gray-600">
                            {deduction.avg_deduction_percentage}% deductible
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Footer with dynamic totals */}
                {activeTab && deductions[activeTab] && (
                  <div className="bg-blue-500 p-5 rounded-xl text-white text-center">
                    <h3 className="text-2xl font-semibold mb-2">
                      ${calculateTotals(deductions[activeTab]).totalDeduction}
                    </h3>
                    <p className="text-base font-medium">
                      Total Potential Deductions ({activeTab})
                    </p>
                    <p className="text-sm mt-1">
                      Estimated Tax Savings: {calculateTotals(deductions[activeTab]).savingsRange}
                    </p>
                    <button className="mt-4 px-4 py-2 rounded-lg bg-white text-blue-500 font-medium">
                      Get Insights
                    </button>
                  </div>
                )}
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default DeductionsWidget;
