import { useState, ChangeEvent, FormEvent, useEffect } from "react";
import "./AuthForm.css";

type ToastType = "success" | "error";

let toastRoot: HTMLElement | null = null;

const showToast = (type: ToastType, message: string): void => {
  if (!toastRoot) {
    toastRoot = document.createElement("div");
    toastRoot.id = "custom-toast-root";
    toastRoot.style.position = "fixed";
    toastRoot.style.top = "20px";
    toastRoot.style.right = "20px";
    toastRoot.style.zIndex = "9999";
    document.body.appendChild(toastRoot);
  }

  const toast = document.createElement("div");
  toast.className = "custom-toast";
  toast.style.marginBottom = "10px";
  toast.style.padding = "12px 20px";
  toast.style.borderRadius = "6px";
  toast.style.minWidth = "250px";
  toast.style.boxShadow = "0 2px 6px rgba(0,0,0,0.15)";
  toast.style.fontWeight = "bold";
  toast.style.transition = "all 0.3s ease";
  toast.style.opacity = "1";

  if (type === "success") {
    toast.style.backgroundColor = "#d1e7dd";
    toast.style.color = "#0f5132";
    toast.style.borderLeft = "6px solid #198754";
  } else if (type === "error") {
    toast.style.backgroundColor = "#f8d7da";
    toast.style.color = "#842029";
    toast.style.borderLeft = "6px solid #dc3545";
  }

  toast.innerText = message;
  toastRoot.appendChild(toast);

  setTimeout(() => {
    toast.style.opacity = "0";
    setTimeout(() => {
      toast.remove();
    }, 300);
  }, 3000);
};

interface FormData {
  userId: string;
  access_token: string;
}

interface FormErrors {
  userId?: string;
  access_token?: string;
}

export default function AuthForm() {
  const [formData, setFormData] = useState<FormData>({
    userId: "",
    access_token: "",
  });
  const [loading, setLoading] = useState<boolean>(false);
  const [errors, setErrors] = useState<FormErrors>({});
  const [widgetError, setWidgetError] = useState<string | null>(null);
  const [showClientAuthForm, setShowClientAuthForm] = useState<boolean>(false);

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setFormData((prev) => ({ ...prev, [name]: value }));

    setErrors((prevErrors) => {
      const newErrors = { ...prevErrors };
      if (newErrors[name as keyof FormErrors]) {
        delete newErrors[name as keyof FormErrors];
      }
      return newErrors;
    });
  };

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setWidgetError(null);
    setLoading(true);

    const newErrors: FormErrors = {};
    if (!formData.userId) newErrors.userId = "User ID is required.";
    if (!formData.access_token)
      newErrors.access_token = "Access Token is required.";

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setShowClientAuthForm(false);
      setLoading(false); // Don't forget to stop loading on error

      // Show toast for each error message
      Object.values(newErrors).forEach((errorMessage) => {
        showToast("error", errorMessage);
      });
    } else {
      setErrors({});
      setShowClientAuthForm(true);
      // Let the widget handle further async work — stop loading right after form show
      setLoading(false);
    }
  };

  // New function to handle the API calls sequence
  const handleGetCredentialsAndGenerateLink = async () => {
    setLoading(true);
    setWidgetError(null);
    try {
      // 1. GET client credentials
      const credentialsResponse = await fetch(
        "https://api-stgbe.musetax.com/mmp/get_client_credentials",
        {
          method: "GET",
          headers: {
            accept: "application/json",
            "ngrok-skip-browser-warning":"69420"

          },
        }
      );
      if (!credentialsResponse.ok) {
        throw new Error(`Failed to get client credentials: ${credentialsResponse.statusText}`);
      }
      const credentialsData = await credentialsResponse.json();
      const { client_id, client_secret } = credentialsData;

      if (!client_id || !client_secret) {
        throw new Error("Client ID or Client Secret missing in response");
      }
      showToast("success", "Client credentials obtained");

      // 2. POST to get token
      const tokenResponse = await fetch(
        "https://api-stgbe.musetax.com/mmp/token",
        {
          method: "POST",
          headers: {
            accept: "application/json",
            "Content-Type": "application/json",
            "ngrok-skip-browser-warning":"69420"

          },
          body: JSON.stringify({
            client_id,
            client_secret,
          }),
        }
      );
      if (!tokenResponse.ok) {
        throw new Error(`Failed to get token: ${tokenResponse.statusText}`);
      }
      const tokenData = await tokenResponse.json();
      const access_token = tokenData.access_token;
      if (!access_token) {
        throw new Error("Access token missing in token response");
      }
      showToast("success", "Access token obtained");

      // Update formData with access_token
      setFormData((prev) => ({ ...prev, access_token }));

      // 3. POST to generate link
      const generateLinkResponse = await fetch(
        "https://api-stgbe.musetax.com/mmp/generate-link",
        {
          method: "POST",
          headers: {
            accept: "application/json",
            "Content-Type": "application/json",
            "ngrok-skip-browser-warning":"69420"
          },
          body: JSON.stringify({
            access_token,
          }),
        }
      );
      if (!generateLinkResponse.ok) {
        throw new Error(`Failed to generate link: ${generateLinkResponse.statusText}`);
      }
      const generateLinkData = await generateLinkResponse.json();
      console.log(generateLinkData);

      showToast("success", "Link generated successfully");
      window.open(`http://localhost:5173/signOn1.html?token=${encodeURIComponent(access_token)}`)

      // Optionally, you can do something with generateLinkData here

    } catch (error: any) {
      setWidgetError(error.message || "An error occurred");
      showToast("error", error.message || "An error occurred");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>


        <>

          <button
            onClick={handleGetCredentialsAndGenerateLink}
            className="submit-btn"
            disabled={loading}
            style={{ marginTop: "20px" }}
          >
            {loading ? "Loading..." : "Get Credentials and Generate Link"}
          </button>
        </>


    </div>
  );
}
