import React, { useEffect, useState } from "react";

interface Props {
  userId: string;
  access_token: string;
  session_token: string;
  onError?: (msg: string) => void;
}

const PotentialDeductions: React.FC<Props> = ({
  userId,
  access_token,
  session_token,
  onError,
}) => {
  const [transactions, setTransactions] = useState<any[]>([]);
  const [activeCategoryIndex, setActiveCategoryIndex] = useState<number>(0);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchTransactions = async (userId: string) => {
    setLoading(true);
    try {
      const  data :any= await fetch(`/potential-deductions/${userId}`,{
        method: "GET",

      });
      console.log(data)
      if (data?.detail) {
        const msg = data.detail;
        setError(msg);
        onError?.(msg);
       } else {
        setTransactions(data?.deductions || []);
      }
    } catch (error: any) {
      const msg = "Failed to fetch transactions.";
      setError(msg);
      onError?.(msg);
     } finally {
      setLoading(false);
    }
  };


  const activeDeduction = transactions[activeCategoryIndex];
  console.log(activeDeduction, "activeDeductionactiveDeductionactiveDeduction");
  console.log(transactions, "transactionstransactionstransactions");

  return (
    <>
      <div
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          width: "60px",
          height: "60px",
          backgroundColor: "#007bff",
          borderRadius: "50%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          cursor: "pointer",
          zIndex: 9999,
          color: "#fff",
          fontSize: "28px",
          boxShadow: "0 4px 5px rgba(0, 0, 0, 0.3)",
        }}
        onClick={() => setIsOpen(!isOpen)}
        title="Open Auth Widget"
      >
        ðŸ’¬
      </div>

      {isOpen && (
        <div
          style={{
            position: "fixed",
            bottom: "90px",
            right: "20px",
            width: "68rem",
            maxHeight: "70vh",
            zIndex: 9998,
            overflowY: "auto",
          }}
        >
          {error ? (
            <div
              style={{
                paddingTop: "200px",
                minHeight: "500px",
              }}
            >
              ''
              {/* <ErrorWidget message={error} onClose={() => setIsOpen(false)} /> */}
            </div>
          ) : (
            <div
              style={{
                maxWidth: 1200,
                margin: "auto",
                border: "1px solid #ccc",
                borderRadius: 16,
                overflow: "hidden",
              }}
            >
              <div
                style={{
                  backgroundColor: "#F1F7FB",
                  padding: "16px 24px",
                  display: "flex",
                  justifyContent: "space-between",
