"use client";

import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

// ---------------- Interfaces ----------------
interface PayrollDetails {
  income_type?: string;
  annual_salary?: number;
  hourly_rate?: number;
  first_name?: string;
  last_name?: string;
  middle_name?: string;
  average_hours_per_week?: number;
  seasonal_variation?: string;
  estimated_annual_income?: number;
  filing_status?: string;
  pay_frequency?: string;
  current_withholding_per_paycheck?: number;
  desired_boost_per_paycheck?: number | null;
  additional_income?: number;
  deductions?: number;
  dependents?: number;
  spouse_income?: number;
}

interface UserAndSessionId {
  payroll_details: PayrollDetails;
  company_name: string;
  first_name: string;
  last_name: string;
  email: string;
}

interface TokenPayload {
  client_id: string;
  client_secret: string;
}

interface FormErrors {
  [key: string]: string;
}

// ---------------- Token & Session Helpers ----------------
const tokenStore = (data: any) => {
  localStorage.setItem("authTokenMuse", data.access_token);
  localStorage.setItem("refreshTokenMuse", data.refresh_token);
};

const tokenCreateFromClientIdAndSecret = async (payload: TokenPayload) => {
  const res = await axios.post(`https://api-stgbe.musetax.com/auth/token`, payload);
  tokenStore(res.data);
  return res.data;
};

const storeSessionId = (session_id: string) => {
  const ONE_DAY_MS = 24 * 60 * 60 * 1000;
  localStorage.setItem("originalSessionId", session_id);
  localStorage.setItem("sessionExpiry", (Date.now() + ONE_DAY_MS).toString());
};

const getUserAndSessionId = async (payload: UserAndSessionId) => {
  const res = await axios.post(`https://api-stgbe.musetax.com/auth/token`, payload);
  storeSessionId(res.data.session_id);
  return res.data;
};

// ---------------- Component ----------------
export default function AuthForm() {
  const [formData, setFormData] = useState({
    client_id: "",
    client_secret: "",
    company_name: "",
    first_name: "",
    last_name: "",
    email: "",
    chattype: "ukg-ready",
    income_type: "",
    annual_salary: "",
    hourly_rate: "",
    average_hours_per_week: "",
    filing_status: "",
    pay_frequency: "",
    current_withholding_per_paycheck: "",
    desired_boost_per_paycheck: "",
    additional_income: "",
    deductions: "",
    dependents: "",
    spouse_income: "",
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [loading, setLoading] = useState(false);
  const [showIframe, setShowIframe] = useState(false);
  const [iframeUrl, setIframeUrl] = useState("");

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};
    if (!formData.client_id.trim()) newErrors.client_id = "Client ID is required.";
    if (!formData.client_secret.trim()) newErrors.client_secret = "Client Secret is required.";
    if (!formData.first_name.trim()) newErrors.first_name = "First Name is required.";
    if (!formData.last_name.trim()) newErrors.last_name = "Last Name is required.";
    if (!formData.email.trim()) newErrors.email = "Email is required.";
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = "Email is invalid.";
    if (!formData.company_name.trim()) newErrors.company_name = "Company Name is required.";

    setErrors(newErrors);
    if (Object.keys(newErrors).length > 0) {
      Object.values(newErrors).forEach((error) => toast.error(error));
      return false;
    }
    return true;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: "" }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      // 1️⃣ Token
      const tokenData = await tokenCreateFromClientIdAndSecret({
        client_id: formData.client_id,
        client_secret: formData.client_secret,
      });
      toast.success("Token created successfully");

      // 2️⃣ Session
      const sessionData = await getUserAndSessionId({
        company_name: formData.company_name,
        first_name: formData.first_name,
        last_name: formData.last_name,
        email: formData.email,
        payroll_details: {
          income_type: formData.income_type || undefined,
          annual_salary: formData.annual_salary ? Number(formData.annual_salary) : undefined,
          hourly_rate: formData.hourly_rate ? Number(formData.hourly_rate) : undefined,
          average_hours_per_week: formData.average_hours_per_week ? Number(formData.average_hours_per_week) : undefined,
          filing_status: formData.filing_status || undefined,
          pay_frequency: formData.pay_frequency || undefined,
          current_withholding_per_paycheck: formData.current_withholding_per_paycheck ? Number(formData.current_withholding_per_paycheck) : undefined,
          desired_boost_per_paycheck: formData.desired_boost_per_paycheck ? Number(formData.desired_boost_per_paycheck) : null,
          additional_income: formData.additional_income ? Number(formData.additional_income) : undefined,
          deductions: formData.deductions ? Number(formData.deductions) : undefined,
          dependents: formData.dependents ? Number(formData.dependents) : undefined,
          spouse_income: formData.spouse_income ? Number(formData.spouse_income) : undefined,
        },
      });
      toast.success("Session created successfully");

      // 3️⃣ Build iframe URL
      const url = new URL("http://localhost:3000/");
      url.searchParams.append("chattype", formData.chattype);
      url.searchParams.append("user_id", sessionData.user_id);
      url.searchParams.append("session_id", sessionData.session_id);
      url.searchParams.append("access_token", tokenData.access_token);
      url.searchParams.append("refresh_token", tokenData.refresh_token);

      setIframeUrl(url.toString());
      setShowIframe(true);
      toast.success("Iframe loaded successfully");
    } catch (err: any) {
      console.error(err);
      toast.error(err.response?.data?.message || err.message || "An error occurred");
    } finally {
      setLoading(false);
    }
  };

  // Re-usable input style with error handling
  const inputClass = (hasError: boolean) =>
    `border rounded-md p-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500 ${
      hasError ? "border-red-500 focus:ring-red-500" : "border-gray-300"
    }`;

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-8">
      <h1 className="text-2xl font-bold text-gray-800">User & Payroll Details</h1>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* API Credentials */}
        <section className="bg-white p-6 rounded-lg shadow space-y-4">
          <h2 className="text-lg font-semibold text-gray-700">API Credentials</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="client_id" className="block text-sm font-medium text-gray-700 mb-1">
                Client ID *
              </label>
              <input
                id="client_id"
                name="client_id"
                type="text"
                placeholder="Enter Client ID"
                value={formData.client_id}
                onChange={handleChange}
                className={inputClass(!!errors.client_id)}
                required
              />
              {errors.client_id && <p className="mt-1 text-sm text-red-600">{errors.client_id}</p>}
            </div>
            <div>
              <label htmlFor="client_secret" className="block text-sm font-medium text-gray-700 mb-1">
                Client Secret *
              </label>
              <input
                id="client_secret"
                name="client_secret"
                type="password"
                placeholder="Enter Client Secret"
                value={formData.client_secret}
                onChange={handleChange}
                className={inputClass(!!errors.client_secret)}
                required
              />
              {errors.client_secret && <p className="mt-1 text-sm text-red-600">{errors.client_secret}</p>}
            </div>
          </div>
        </section>

        {/* User Details */}
        <section className="bg-white p-6 rounded-lg shadow space-y-4">
          <h2 className="text-lg font-semibold text-gray-700">User Details</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label htmlFor="first_name" className="block text-sm font-medium text-gray-700 mb-1">
                First Name *
              </label>
              <input
                id="first_name"
                name="first_name"
                type="text"
                placeholder="Enter First Name"
                value={formData.first_name}
                onChange={handleChange}
                className={inputClass(!!errors.first_name)}
                required
              />
              {errors.first_name && <p className="mt-1 text-sm text-red-600">{errors.first_name}</p>}
            </div>
            <div>
              <label htmlFor="last_name" className="block text-sm font-medium text-gray-700 mb-1">
                Last Name *
              </label>
              <input
                id="last_name"
                name="last_name"
                type="text"
                placeholder="Enter Last Name"
                value={formData.last_name}
                onChange={handleChange}
                className={inputClass(!!errors.last_name)}
                required
              />
              {errors.last_name && <p className="mt-1 text-sm text-red-600">{errors.last_name}</p>}
            </div>
            <div>
              <label htmlFor="company_name" className="block text-sm font-medium text-gray-700 mb-1">
                Company Name *
              </label>
              <input
                id="company_name"
                name="company_name"
                type="text"
                placeholder="Enter Company Name"
                value={formData.company_name}
                onChange={handleChange}
                className={inputClass(!!errors.company_name)}
                required
              />
              {errors.company_name && <p className="mt-1 text-sm text-red-600">{errors.company_name}</p>}
            </div>
            <div className="md:col-span-2 lg:col-span-3">
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                Email *
              </label>
              <input
                id="email"
                name="email"
                type="email"
                placeholder="Enter Email"
                value={formData.email}
                onChange={handleChange}
                className={inputClass(!!errors.email)}
                required
              />
              {errors.email && <p className="mt-1 text-sm text-red-600">{errors.email}</p>}
            </div>
          </div>
        </section>

        {/* Payroll Details */}
        <section className="bg-white p-6 rounded-lg shadow space-y-4">
          <h2 className="text-lg font-semibold text-gray-700">Payroll Details (Optional)</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label htmlFor="income_type" className="block text-sm font-medium text-gray-700 mb-1">
                Income Type
              </label>
              <input
                id="income_type"
                name="income_type"
                type="text"
                placeholder="e.g., Salary, Hourly"
                value={formData.income_type}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="annual_salary" className="block text-sm font-medium text-gray-700 mb-1">
                Annual Salary
              </label>
              <input
                id="annual_salary"
                name="annual_salary"
                type="number"
                placeholder="Enter Annual Salary"
                value={formData.annual_salary}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="hourly_rate" className="block text-sm font-medium text-gray-700 mb-1">
                Hourly Rate
              </label>
              <input
                id="hourly_rate"
                name="hourly_rate"
                type="number"
                placeholder="Enter Hourly Rate"
                value={formData.hourly_rate}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="average_hours_per_week" className="block text-sm font-medium text-gray-700 mb-1">
                Average Hours per Week
              </label>
              <input
                id="average_hours_per_week"
                name="average_hours_per_week"
                type="number"
                placeholder="e.g., 40"
                value={formData.average_hours_per_week}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="filing_status" className="block text-sm font-medium text-gray-700 mb-1">
                Filing Status
              </label>
              <input
                id="filing_status"
                name="filing_status"
                type="text"
                placeholder="e.g., Single, Married"
                value={formData.filing_status}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="pay_frequency" className="block text-sm font-medium text-gray-700 mb-1">
                Pay Frequency
              </label>
              <input
                id="pay_frequency"
                name="pay_frequency"
                type="text"
                placeholder="e.g., Bi-weekly"
                value={formData.pay_frequency}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="current_withholding_per_paycheck" className="block text-sm font-medium text-gray-700 mb-1">
                Current Withholding per Paycheck
              </label>
              <input
                id="current_withholding_per_paycheck"
                name="current_withholding_per_paycheck"
                type="number"
                placeholder="Enter amount"
                value={formData.current_withholding_per_paycheck}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="desired_boost_per_paycheck" className="block text-sm font-medium text-gray-700 mb-1">
                Desired Boost per Paycheck
              </label>
              <input
                id="desired_boost_per_paycheck"
                name="desired_boost_per_paycheck"
                type="number"
                placeholder="Enter amount"
                value={formData.desired_boost_per_paycheck}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="additional_income" className="block text-sm font-medium text-gray-700 mb-1">
                Additional Income
              </label>
              <input
                id="additional_income"
                name="additional_income"
                type="number"
                placeholder="Enter amount"
                value={formData.additional_income}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="deductions" className="block text-sm font-medium text-gray-700 mb-1">
                Deductions
              </label>
              <input
                id="deductions"
                name="deductions"
                type="number"
                placeholder="Enter amount"
                value={formData.deductions}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="dependents" className="block text-sm font-medium text-gray-700 mb-1">
                Dependents
              </label>
              <input
                id="dependents"
                name="dependents"
                type="number"
                placeholder="Number of dependents"
                value={formData.dependents}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
            <div>
              <label htmlFor="spouse_income" className="block text-sm font-medium text-gray-700 mb-1">
                Spouse Income
              </label>
              <input
                id="spouse_income"
                name="spouse_income"
                type="number"
                placeholder="Enter amount"
                value={formData.spouse_income}
                onChange={handleChange}
                className={inputClass(false)}
              />
            </div>
          </div>
        </section>

        {/* Submit */}
        <div className="flex justify-end">
          <button
            type="submit"
            disabled={loading}
            className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 disabled:bg-blue-400 transition flex items-center gap-2"
          >
            {loading ? (
              <>
                <svg className="animate-spin h-4 w-4" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                </svg>
                Loading...
              </>
            ) : (
              "Open Iframe"
            )}
          </button>
        </div>
      </form>

      {/* Iframe - Responsive */}
      {showIframe && iframeUrl && (
        <section className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-lg font-semibold text-gray-700 mb-4">Embedded Iframe</h2>
          <div className="w-full">
            <iframe
              src={iframeUrl}
              title="User Payroll Iframe"
              className="w-full rounded-lg border border-gray-300"
              style={{ height: "80vh", minHeight: "600px" }}
              allow="clipboard-write; microphone; camera"
            />
          </div>
        </section>
      )}
    </div>
  );
}
