"use client";

import React, { useState } from "react";
import axios from "axios";

// ---------------- Interfaces ----------------
interface PayrollDetails {
  income_type?: string;
  annual_salary?: number;
  hourly_rate?: number;
  first_name?: string;
  last_name?: string;
  middle_name?: string;
  average_hours_per_week?: number;
  seasonal_variation?: string;
  estimated_annual_income?: number;
  filing_status?: string;
  pay_frequency?: string;
  current_withholding_per_paycheck?: number;
  desired_boost_per_paycheck?: number | null;
  additional_income?: number;
  deductions?: number;
  dependents?: number;
  spouse_income?: number;
}

interface UserAndSessionId {
  payroll_details: PayrollDetails;
  company_name: string;
  first_name: string;
  last_name: string;
  email: string;
}

interface TokenPayload {
  client_id: string;
  client_secret: string;
}

// ---------------- Token & Session Helpers ----------------
const tokenStore = (data: any) => {
  localStorage.setItem("authTokenMuse", data.access_token);
  localStorage.setItem("refreshTokenMuse", data.refresh_token);
};

const tokenCreateFromClientIdAndSecret = async (payload: TokenPayload) => {
  const res = await axios.post(`https://api-stgbe.musetax.com/auth/token`, payload);
  tokenStore(res.data);
  return res.data;
};

const storeSessionId = (session_id: string) => {
  const ONE_DAY_MS = 24 * 60 * 60 * 1000;
  localStorage.setItem("originalSessionId", session_id);
  localStorage.setItem("sessionExpiry", (Date.now() + ONE_DAY_MS).toString());
};

const getUserAndSessionId = async (payload: UserAndSessionId) => {
  const res = await axios.post(`https://api-stgbe.musetax.com/auth/token`, payload);
  storeSessionId(res.data.session_id);
  return res.data;
};

// ---------------- Component ----------------
export default function AuthForm() {
  const [formData, setFormData] = useState({
    client_id: "",
    client_secret: "",
    company_name: "",
    first_name: "",
    last_name: "",
    email: "",
    chattype: "ukg-ready",
    income_type: "",
    annual_salary: "",
    hourly_rate: "",
    average_hours_per_week: "",
    filing_status: "",
    pay_frequency: "",
    current_withholding_per_paycheck: "",
    desired_boost_per_paycheck: "",
    additional_income: "",
    deductions: "",
    dependents: "",
    spouse_income: "",
  });

  const [showIframe, setShowIframe] = useState(false);
  const [iframeUrl, setIframeUrl] = useState("");

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      // 1Ô∏è‚É£ Token
      const tokenData = await tokenCreateFromClientIdAndSecret({
        client_id: formData.client_id,
        client_secret: formData.client_secret,
      });

      // 2Ô∏è‚É£ Session
      const sessionData = await getUserAndSessionId({
        company_name: formData.company_name,
        first_name: formData.first_name,
        last_name: formData.last_name,
        email: formData.email,
        payroll_details: {
          income_type: formData.income_type,
          annual_salary: Number(formData.annual_salary),
          hourly_rate: Number(formData.hourly_rate),
          average_hours_per_week: Number(formData.average_hours_per_week),
          filing_status: formData.filing_status,
          pay_frequency: formData.pay_frequency,
          current_withholding_per_paycheck: Number(formData.current_withholding_per_paycheck),
          desired_boost_per_paycheck: Number(formData.desired_boost_per_paycheck),
          additional_income: Number(formData.additional_income),
          deductions: Number(formData.deductions),
          dependents: Number(formData.dependents),
          spouse_income: Number(formData.spouse_income),
        },
      });

      // 3Ô∏è‚É£ Build iframe URL
      const url = new URL("http://localhost:3000/");
      url.searchParams.append("chattype", formData.chattype);
      url.searchParams.append("user_id", sessionData.user_id);
      url.searchParams.append("session_id", sessionData.session_id);
      url.searchParams.append("access_token", tokenData.access_token);
      url.searchParams.append("refresh_token", tokenData.refresh_token);

      setIframeUrl(url.toString());
      setShowIframe(true);
    } catch (err) {
      console.error(err);
    }
  };

  // Re-usable input style
  const inputClass =
    "border rounded-md p-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500";

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-8">
      <h1 className="text-2xl font-bold text-gray-800">User & Payroll Details</h1>

      {/* API Credentials */}
      <section className="bg-white p-6 rounded-lg shadow space-y-4">
        <h2 className="text-lg font-semibold text-gray-700">API Credentials</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input name="client_id" placeholder="Client ID" value={formData.client_id} onChange={handleChange} className={inputClass}/>
          <input name="client_secret" placeholder="Client Secret" value={formData.client_secret} onChange={handleChange} className={inputClass}/>
        </div>
      </section>

      {/* User Details */}
      <section className="bg-white p-6 rounded-lg shadow space-y-4">
        <h2 className="text-lg font-semibold text-gray-700">User Details</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <input name="first_name" placeholder="First Name" value={formData.first_name} onChange={handleChange} className={inputClass}/>
          <input name="last_name" placeholder="Last Name" value={formData.last_name} onChange={handleChange} className={inputClass}/>
          <input name="company_name" placeholder="Company Name" value={formData.company_name} onChange={handleChange} className={inputClass}/>
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} className={inputClass}/>
        </div>
      </section>

      {/* Payroll Details */}
      <section className="bg-white p-6 rounded-lg shadow space-y-4">
        <h2 className="text-lg font-semibold text-gray-700">Payroll Details (Optional)</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          <input name="income_type" placeholder="Income Type" value={formData.income_type} onChange={handleChange} className={inputClass}/>
          <input name="annual_salary" type="number" placeholder="Annual Salary" value={formData.annual_salary} onChange={handleChange} className={inputClass}/>
          <input name="hourly_rate" type="number" placeholder="Hourly Rate" value={formData.hourly_rate} onChange={handleChange} className={inputClass}/>
          <input name="average_hours_per_week" type="number" placeholder="Avg Hours/Week" value={formData.average_hours_per_week} onChange={handleChange} className={inputClass}/>
          <input name="filing_status" placeholder="Filing Status" value={formData.filing_status} onChange={handleChange} className={inputClass}/>
          <input name="pay_frequency" placeholder="Pay Frequency" value={formData.pay_frequency} onChange={handleChange} className={inputClass}/>
          <input name="current_withholding_per_paycheck" type="number" placeholder="Current Withholding/Paycheck" value={formData.current_withholding_per_paycheck} onChange={handleChange} className={inputClass}/>
          <input name="desired_boost_per_paycheck" type="number" placeholder="Desired Boost/Paycheck" value={formData.desired_boost_per_paycheck} onChange={handleChange} className={inputClass}/>
          <input name="additional_income" type="number" placeholder="Additional Income" value={formData.additional_income} onChange={handleChange} className={inputClass}/>
          <input name="deductions" type="number" placeholder="Deductions" value={formData.deductions} onChange={handleChange} className={inputClass}/>
          <input name="dependents" type="number" placeholder="Dependents" value={formData.dependents} onChange={handleChange} className={inputClass}/>
          <input name="spouse_income" type="number" placeholder="Spouse Income" value={formData.spouse_income} onChange={handleChange} className={inputClass}/>
        </div>
      </section>

      {/* Submit */}
      <div className="flex justify-end">
        <button
          onClick={handleSubmit}
          className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 transition"
        >
          Open Iframe
        </button>
      </div>

      {/* Iframe */}
      {showIframe && iframeUrl && (
        <iframe
          src={iframeUrl}
          title="User Payroll Iframe"
          width="100%"
          height="700"
          className="mt-6 rounded-lg border border-gray-300"
          allow="clipboard-write; microphone; camera"
        />
      )}
    </div>
  );
}
